import React, { useState } from "react";

function CreateArea(props) {

  const [inputContent, setInputContent] = useState({title: "",content: ""});

  const handleChange = event => {
    const {name, value} = event.target;
    setInputContent(prevState => ({
      ...prevState,
      [name]: value
    }));
  }

  return (
    <div>
      <form>
        <input value={inputContent.title} onChange={handleChange} name="title" placeholder="Title" autoComplete="off" />
        <textarea value={inputContent.content} onChange={handleChange} name="content" placeholder="Take a note..." rows="3" />
        <button onClick={() => {
          props.onAdd(inputContent);
          //setInputContent({title: "",content: ""});
        }}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
